


# Project Euler: 25
# What is the index of the first term in the Fibonacci sequence to contain 1000 digits?

import time

def euler_25(n): 
  cont=2
  for i in gen_fib():
    cont+=1
    if len(str(i)) >= n:
      return i,len(str(i)),cont
  return 0,0,0

def gen_fib():
  a, b = 1,1
  while True:
    a, b = b, a+b
    yield b
  

##############################################

if __name__ == "__main__":

  start=time.time()
  number = 1000

  a,b,c = euler_25(number)

  print("N째 index fibonacci number {} (Length:{} digits):\t{}".format(a,b,c))

  print("\nTiempo de operaci처n:",time.time()-start,"seg.")
    
